var View = require("View");

var Expandable = module.exports = View.AutoSub.extend({
	name: "Expandable",
	addClass: "expandable",
	Preview: View.AutoSub.extend({
		name: "Preview",
		addClass: "preview"
	}),
	Contents: View.AutoSub.extend({
		name: "Contents",
		addClass: "contents",
		css: {
			paddingLeft: "1em"
		}
	}),
	content: function(){
		this.preview.render();
		this.contents.hide().render();
	},
	behaviors: function(){
		var expandable = this;
		this.preview.click(function(){
			expandable.contents.slideToggle();
		});
	}
});