var Base3 = require("../Base3/Base3");
var View = require("View");
require("font-awesome-webpack");
var setup = require("Base3/setup");
var is = require("is");

var Icon = module.exports = View.AutoSub.extend({
	name: "Icon",
	tag: "i",
	addClass: "icon fa fa-fw",
	set: {
		other: function(icon, value){
			if (is.str(value))
				icon.type(value);
			else if (is.bool(value) && !value)
				icon.active = false;
			else
				console.error("not supported");
		}
	},
	type: function(type){
		this.active = true;
		this.$el.removeClass("fa-" + this._type).addClass("fa-" + type);
		this._type = type;
		return this;
	}
});