var Base3 = require("../Base3/Base3");
var View = require("View");
require("font-awesome-webpack");
var setup = require("Base3/setup");

var Icon = module.exports = View.AutoSub.extend({
	name: "Icon",
	tag: "i",
	addClass: "icon fa fa-fw",
	set: {
		str: function(icon, type){
			icon.type(type);
		},
		bool: function(icon, bool){
			if (bool)
				console.warn("not supported");
			else
				icon.active = false;
		}
	},
	type: function(type){
		this.active = true;
		this.$el.removeClass("fa-" + this._type).addClass("fa-" + type);
		this._type = type;
		return this;
	}
});