var Base3 = require("../Base3/Base3");
var setup = require("Base3/setup");
var View = require("View");
require("font-awesome-webpack");
var Icon = require("./Icon");
var is = require("../is");

var Item = module.exports = View.extend({
	name: "Item",
	addClass: "item flex pad-children",
	set: {
		other: function(item, val){
			item.label.set(val);
			item.active = true;
		}
	},
	Icon: Icon.extend({
		name: "Icon",
		type: "folder"
	}),
	Value: View.extend({
		name: "Value",
		addClass: "value",
		active: false
	}),
	Label: View.extend({
		name: "Label",
		addClass: "label",
		content: "Item"
	}),
	content: function(){
		this.icon.render();
		this.label.render();
		this.value.render();
	}
});