var is = require("is");
var Base = require("base42");
var ModFn = Base.extend({
	name: "ModFn",
	init: function(){
		this.init_fn();
	},
	init_fn: function(){
		var self = this;
		this.fn = function(){
			return self.main.apply(self, [this].concat([].slice.call(arguments)));
		};
		this.fn.modFn = this;
		this.fn.set = function(o){
			var MFn = this.modFn.constructor;
			var NewModFn = MFn.extend({ name: MFn.name }, o);
			return new NewModFn().fn;
		};
	},
	main: function(ctx){
		var args = this.args(arguments); // just chops off the first argument, the ctx, so you can "apply" the args
	},
	args: function(args){
		return [].slice.call(args, 1);
	}
});

module.exports = ModFn;