var TestFramework = require("../test/framework");
var test = TestFramework.test;
var assert = console.assert.bind(console);
var is = require("../is");
var $ = require("jquery");
var View = require("./View");
var Div = require("./Div");

var xtest = function(){};

$(function(){

test("View", function(){

	var basic = function(ViewClass){
		test("string", function(){
			ViewClass("hello world").css("color", "red");
		});

		test("function", function(){
			ViewClass(function(){
				this.css("color", "blue").append("hello world");
			});
		});

		test("object", function(){
			ViewClass({
				content: "hello world",
				css: {
					color: "green"
				}
			});
		});

		test("logging", function(){
			ViewClass({
				content: "logging turned on",
				css: ["color", "orange"],
				log: true
			})
		});
	};

	test("basic", function(){
		basic(View);
	});

	test("extending", function(){

		test("empty", function(){
			var View2 = View.extend();

			test("basic", function(){
				basic(View2);
			});
		});

		test("logging", function(){
			var View2 = View.extend({
				log: true
			});

			test("basic", function(){
				basic(View2);
			});
		});

	});

});

});