var Mod2 = require("./Mod2");
var track = require("track")

var Mod3 = module.exports = Mod2.extend({
	name: "Mod3",
	instantiate: function(o){
		track(this);
		this.setup_subs();
		this.inst && this.inst();
		this.set.apply(this, arguments);
		this.initialize();
	},
	setup_subs: function(){
		for (var i in this){
			if (i === "constructor")
				continue;
			if (this[i].setup)
				this[i].setup(this, i);
		}
	}
});