var Mod2 = require("./Mod2");
var track = require("track")

var Mod3 = module.exports = Mod2.extend({
	name: "Mod3",
	instantiate: function(o){
		this.inst && this.inst();
		this.protect();
		this.set.apply(this, arguments);
		this.initialize();
	},
	protect: function(){
		for (var i in this){
			if (["constructor", "base", "proto", "parent"].indexOf(i) > -1)
				continue;
			if (this[i] && this[i].clone && !this.hasOwnProperty(i)){
				this[i] = this[i].clone({
					parent: this
				});
			}
		}
	},
	setup: function(parent, name){
		// this will run whenever an instance is simply added to another...
		// it might just be a reference
		if (!this.parent)
			this.parent = parent;

		if (!this.name)
			this.name = name;
	},
	clone: function(){
		var clone = Object.create(this);
		track(clone);
		clone.instantiate.apply(clone, arguments);
		return clone;
	}
});