var View = require("View");
var Item = require("Item");
var Expandable = require("Expandable");

var RouteView = module.exports = Expandable.extend({
	name: "RouteView",
	addClass: "route light",
	preview: function(){
		var route = this.parent;
		this.item = Item({
			icon: "bullseye",
			label: route.pathname
		});
	}
})