var View = require("View");
var Item = require("Item");
var Expandable = require("Expandable");

var $ = require("jquery");

var RouterView = module.exports = Expandable.extend({
	name: "RouterView",
	addClass: "router light",
	init: function(){
		this.render();
	},
	Preview: {
		content: function(){
			var router = this.parent;
			console.error("seriously, wtf", router.name)
			this.item = Item({
				icon: "code-fork",
				label: router.name
			});
		}
	},
	rendered: function(){
		var view = this;
		$(function(){
			view.prependTo("body");
		})
	}
})